import React, { useState } from 'react';
import {
  ThemeProvider,
  createTheme,
  CssBaseline,
  Container,
  Box,
  Typography,
  Tabs,
  Tab,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Checkbox,
  Grid,
  Chip,
  Fab,
} from '@mui/material';
import { motion } from 'framer-motion';
import InfoIcon from '@mui/icons-material/Info';
import SportsCricketIcon from '@mui/icons-material/SportsCricket';
import { players } from './data/players';
import { Player, PlayerCategory, PlayerSubCategory } from './types';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#00ff9f',
    },
    secondary: {
      main: '#ff00ff',
    },
    background: {
      default: '#0a1929',
      paper: '#132f4c',
    },
  },
  typography: {
    fontFamily: '"Poppins", "Roboto", "Helvetica", "Arial", sans-serif',
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          background: 'rgba(19, 47, 76, 0.8)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          borderRadius: '16px',
          transition: 'transform 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-5px)',
          },
        },
      },
    },
  },
});

interface PlayerStatsDialogProps {
  player: Player;
  open: boolean;
  onClose: () => void;
}

const PlayerStatsDialog: React.FC<PlayerStatsDialogProps> = ({ player, open, onClose }) => {
  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>{player.name} - Advanced Statistics</DialogTitle>
      <DialogContent>
        <Box sx={{ mt: 2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography variant="h6" gutterBottom>
                {player.team}
              </Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography variant="subtitle1">Matches</Typography>
              <Typography variant="body1">{player.performance.matches}</Typography>
            </Grid>
            {player.performance.runs && (
              <>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">Runs</Typography>
                  <Typography variant="body1">{player.performance.runs}</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">Batting Average</Typography>
                  <Typography variant="body1">{player.performance.battingAverage}</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">Strike Rate</Typography>
                  <Typography variant="body1">{player.performance.strikeRate}</Typography>
                </Grid>
              </>
            )}
            {player.performance.wickets && (
              <>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">Wickets</Typography>
                  <Typography variant="body1">{player.performance.wickets}</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">Bowling Average</Typography>
                  <Typography variant="body1">{player.performance.bowlingAverage}</Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">Economy</Typography>
                  <Typography variant="body1">{player.performance.economy}</Typography>
                </Grid>
              </>
            )}
            {player.performance.catches && (
              <Grid item xs={6}>
                <Typography variant="subtitle1">Catches</Typography>
                <Typography variant="body1">{player.performance.catches}</Typography>
              </Grid>
            )}
            {player.performance.stumpings && (
              <Grid item xs={6}>
                <Typography variant="subtitle1">Stumpings</Typography>
                <Typography variant="body1">{player.performance.stumpings}</Typography>
              </Grid>
            )}
          </Grid>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
      </DialogActions>
    </Dialog>
  );
};

const App: React.FC = () => {
  const [selectedCategory, setSelectedCategory] = useState<PlayerCategory>(
    PlayerCategory.KEEPER
  );
  const [selectedSubCategory, setSelectedSubCategory] = useState<
    PlayerSubCategory | undefined
  >(undefined);
  const [selectedPlayer, setSelectedPlayer] = useState<Player | null>(null);
  const [selectedPlayers, setSelectedPlayers] = useState<Set<string>>(new Set());
  const [showFinal11, setShowFinal11] = useState(false);

  const filteredPlayers = players
    .filter((player) => player.category === selectedCategory)
    .filter(
      (player) =>
        !selectedSubCategory || player.subCategory === selectedSubCategory
    )
    .sort((a, b) => {
      // Sort based on performance metrics
      if (selectedCategory === PlayerCategory.KEEPER) {
        return (b.performance.runs || 0) - (a.performance.runs || 0);
      } else if (selectedCategory === PlayerCategory.OPENING_BATTER) {
        return (b.performance.runs || 0) - (a.performance.runs || 0);
      } else if (selectedCategory === PlayerCategory.ALLROUNDER) {
        return (
          (b.performance.wickets || 0) + (b.performance.runs || 0) -
          (a.performance.wickets || 0) - (a.performance.runs || 0)
        );
      } else {
        return (b.performance.wickets || 0) - (a.performance.wickets || 0);
      }
    });

  const subCategories: Record<PlayerCategory, PlayerSubCategory[]> = {
    [PlayerCategory.KEEPER]: [],
    [PlayerCategory.OPENING_BATTER]: [],
    [PlayerCategory.ALLROUNDER]: [
      PlayerSubCategory.SPINNER_ALLROUNDER,
      PlayerSubCategory.FAST_BOWLING_ALLROUNDER,
    ],
    [PlayerCategory.BOWLER]: [
      PlayerSubCategory.SPINNER,
      PlayerSubCategory.FAST_BOWLER,
    ],
  };

  const handleCategoryChange = (event: React.SyntheticEvent, newValue: PlayerCategory) => {
    setSelectedCategory(newValue);
    setSelectedSubCategory(undefined);
  };

  const handleSubCategoryChange = (subCategory: PlayerSubCategory) => {
    setSelectedSubCategory(subCategory);
  };

  const handlePlayerSelect = (playerId: string) => {
    const newSelected = new Set(selectedPlayers);
    if (newSelected.has(playerId)) {
      newSelected.delete(playerId);
    } else {
      newSelected.add(playerId);
    }
    setSelectedPlayers(newSelected);
  };

  const handlePlayerClick = (player: Player) => {
    setSelectedPlayer(player);
  };

  const handleCloseDialog = () => {
    setSelectedPlayer(null);
  };

  const getSelectedPlayersList = () => {
    return players.filter((player) => selectedPlayers.has(player.id));
  };

  const renderFinal11 = () => {
    const selectedList = getSelectedPlayersList();
    return (
      <Box sx={{ py: 4 }}>
        <Typography variant="h4" gutterBottom>
          Your Final 11
        </Typography>
        <List>
          {selectedList.map((player) => (
            <ListItem key={player.id}>
              <ListItemText
                primary={player.name}
                secondary={`${player.team} - ${player.category.replace(/_/g, ' ')}`}
              />
              <ListItemSecondaryAction>
                <IconButton
                  edge="end"
                  aria-label="info"
                  onClick={() => handlePlayerClick(player)}
                >
                  <InfoIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
      </Box>
    );
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ py: 4 }}>
          <Typography
            variant="h3"
            component="h1"
            gutterBottom
            sx={{
              textAlign: 'center',
              mb: 4,
              background: 'linear-gradient(45deg, #00ff9f 30%, #ff00ff 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
            }}
          >
            Domestic Dream11
          </Typography>

          {!showFinal11 ? (
            <>
              <Paper
                sx={{
                  p: 2,
                  mb: 4,
                  background: 'rgba(19, 47, 76, 0.8)',
                  backdropFilter: 'blur(10px)',
                }}
              >
                <Tabs
                  value={selectedCategory}
                  onChange={handleCategoryChange}
                  variant="scrollable"
                  scrollButtons="auto"
                  sx={{
                    '& .MuiTab-root': {
                      color: 'rgba(255, 255, 255, 0.7)',
                      '&.Mui-selected': {
                        color: '#00ff9f',
                      },
                    },
                  }}
                >
                  <Tab label="Keepers" value={PlayerCategory.KEEPER} />
                  <Tab label="Opening Batters" value={PlayerCategory.OPENING_BATTER} />
                  <Tab label="Allrounders" value={PlayerCategory.ALLROUNDER} />
                  <Tab label="Bowlers" value={PlayerCategory.BOWLER} />
                </Tabs>

                {subCategories[selectedCategory].length > 0 && (
                  <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                    {subCategories[selectedCategory].map((subCategory: PlayerSubCategory) => (
                      <Chip
                        key={subCategory}
                        label={subCategory.replace(/_/g, ' ')}
                        onClick={() => handleSubCategoryChange(subCategory)}
                        color={selectedSubCategory === subCategory ? 'primary' : 'default'}
                        sx={{ m: 0.5 }}
                      />
                    ))}
                  </Box>
                )}
              </Paper>

              <List>
                {filteredPlayers.map((player) => (
                  <ListItem
                    key={player.id}
                    component={motion.div}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                  >
                    <Checkbox
                      edge="start"
                      checked={selectedPlayers.has(player.id)}
                      onChange={() => handlePlayerSelect(player.id)}
                    />
                    <ListItemText
                      primary={player.name}
                      secondary={`${player.team} - ${player.category.replace(/_/g, ' ')}`}
                    />
                    <ListItemSecondaryAction>
                      <IconButton
                        edge="end"
                        aria-label="info"
                        onClick={() => handlePlayerClick(player)}
                      >
                        <InfoIcon />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>

              <Fab
                color="primary"
                sx={{ position: 'fixed', bottom: 16, right: 16 }}
                onClick={() => setShowFinal11(true)}
              >
                <SportsCricketIcon />
              </Fab>
            </>
          ) : (
            <>
              <Button
                variant="contained"
                onClick={() => setShowFinal11(false)}
                sx={{ mb: 2 }}
              >
                Back to Selection
              </Button>
              {renderFinal11()}
            </>
          )}
        </Box>
      </Container>

      {selectedPlayer && (
        <PlayerStatsDialog
          player={selectedPlayer}
          open={!!selectedPlayer}
          onClose={handleCloseDialog}
        />
      )}
    </ThemeProvider>
  );
};

export default App;
